import React, { Component } from "react";
import Container from "@material-ui/core/Container";
import MenuItem from "@material-ui/core/MenuItem";
import Select from "@material-ui/core/Select";
import classes from "./ExchangeRate.module.css";
import axios from "axios";
import * as actions from "../../store/actions/index";
import { connect } from "react-redux";


class ExchangeRate extends Component {
  state = {
    from: "GB",
    to: "US"
  };

  componentDidMount() {
    this.props.loadCountries()
  }

  handleChange = name => event => {
    this.setState({ ...this.state, [name]: event.target.value });
  };

  render() {
    return (
      <Container className={classes.Container}>
        <form noValidate autoComplete="off">
          <Select
            id="from-currency"
            select
            label="from-currency"
            value={this.state.from}
            onChange={this.handleChange("from")}
            menuprops= {{
              className: classes.menu
            }}
            helpertext="Please select your base Currency"
            labelWidth={100}
          >
            {this.props.countries.map(country => {
              const currency = country.currencies[0];
              return (
                <MenuItem key={country.alpha2Code} value={country.alpha2Code}>
                  {currency.name + " (" + currency.code + ")"}
                </MenuItem>
              );
            })}
          </Select> 
          <div className={classes.MiddleLabel}> to </div>
          <Select
            id="to-currency"
            select
            label="to-currency"
            value={this.state.to}
            onChange={this.handleChange("to")}
            menuprops= {{
              className: classes.menu
            }}
            helpertext="Please select the currency you want to transform."
            labelWidth={100}
          >
            {this.props.countries.map(country => {
              const currency = country.currencies[0];
              return (
                <MenuItem key={country.alpha2Code} value={country.alpha2Code}>
                  {currency.name + " (" + currency.code + ")"}
                </MenuItem>
              );
            })}
          </Select> 
        </form>
      </Container>
    );
  }
}

const mapStateToProps = state => {
  return {
    countries: state.countries.countries,
    countriesLoading: state.countries.loading
  }
}

const mapDispatchToProps = dispatch => {
  return {
    loadCountries: () => dispatch(actions.fetchCountriesInit())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExchangeRate);
