import React, { Component } from "react";
import { TextField, MenuItem, Select } from "@material-ui/core";
import classes from "./ExchangeRate.module.css";
import axios from "axios";
import * as actions from "../../store/actions/index";
import { connect } from "react-redux";


class ExchangeRate extends Component {
  state = {
    from: "GB",
    to: "US",
    countries: {}
  };

  componentDidMount() {
    this.props.loadCountries()
  }

  handleChange = name => event => {
    this.setState({ ...this.state, [name]: event.target.value });
  };

  render() {
    return (
      <form className={classes.container} noValidate autoComplete="off">
        <Select
          id="standard-select-currency"
          select
          label="Select"
          className={classes.textField}
          value={this.state.from}
          onChange={this.handleChange("from")}
          SelectProps={{
            MenuProps: {
              className: classes.menu
            }
          }}
          helperText="Please select your currency"
          margin="normal"
        >
          {this.props.countries.map(country => {
            return (
              <MenuItem key={country.alpha2Code} value={option.alpha2Code}>
                {country.currencies[0].name}
              </MenuItem>
            );
          })}
        </Select>
      </form>
    );
  }
}

const mapStateToProps = state => {
  return {
    countries: state.countries.countries,
    countriesLoading: state.countries.loading
  }
}

const mapDispatchToProps = dispatch => {
  return {
    loadCountries: () => dispatch(actions.fetchCountriesInit())
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExchangeRate);
